class UserModel { final String uid; final String email; final String? displayName; final String? photoUrl; UserModel({ required this.uid, required this.email, this.displayName, this.photoUrl }); Map<String,dynamic> toMap()=>{'uid':uid,'email':email,'displayName':displayName,'photoUrl':photoUrl}; factory UserModel.fromMap(Map<String,dynamic> m)=>UserModel(uid:m['uid'],email:m['email'],displayName:m['displayName'],photoUrl:m['photoUrl']); }
