class Validators { static String? email(String? v) { if (v==null||v.isEmpty) return 'Email required'; final re = RegExp(r"^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}\$"); return re.hasMatch(v)? null: 'Invalid email'; } static String? password(String? v){ if (v==null||v.isEmpty) return 'Password required'; if (v.length<6) return 'Minimum 6 characters'; return null; } }
